import React, {Component} from "react";
import './tabata.css';
import './components/formInput.jxs'

// declare all variables
let session_length;
let break_length;
let num_sessions;
let current_session;
let time_on;
let current_time;
let timer;
let break_on;


// update variables on screen
function updateScreen() {

}




class Tabata extends Component {
    constructor(props) {
        super(props);
        this.state = {
            session_length: 60,
            break_length: 20,
            num_sessions: 5,
            break_on: false,
            time_on: false,
            current_time: 60,
            current_session: 0
        };

        clearInterval(timer);
    }


    componentDidMount(){
        const {session_length} = this.state.session_length;
        this.setState({
            current_time: session_length
        });
        this.myInterval = setInterval(()=> {
            this.setState(prevState =>({
                current_time: prevState.current_time - 1
            }))
        }, 1000)

    }

    render() {

        return (
                <div>
                    <div id="content">
                        <div id="options">
                            <div className="timer-option">
                                <div>Ejercicio</div>
                                <button className="length-setter">-</button>
                                <input type="text" name="work-length" value={this.state.session_length}/>
                                    <button className="length-setter">+</button>
                            </div>

                            <div className="timer-option">
                                <div>Preparación</div>
                                <button className="length-setter">-</button>
                                <input type="text" name="short-break-length" value={this.state.break_length}/>
                                    <button className="length-setter">+</button>
                            </div>

                            <div className="timer-option">
                                <div>Descanso</div>
                                <button className="length-setter">-</button>
                                <input type="text" name="long-break-length" value={this.state.break_length}/>
                                    <button className="length-setter">+</button>
                            </div>
                        </div>

                        <div>
                            <div id="timer-container">
                                <span id="timer"  className="large">
                                    {current_time}
                                </span>
                                <canvas id="timer-canvas" width="240" height="240"/>
                            </div>
                        </div>
                    </div>
                </div>
        );
    }
}
export default Tabata;
