import React, {Component} from "react";
import './tabata.css';
import './components/formInput.jxs'

// declare all variables
let session_length;
let break_length;
let num_sessions;
let current_session;
let time_on;
let current_time;
let timer;
let break_on;


// update variables on screen
function updateScreen() {

}

function clickCircle() {
    if (!time_on) {
        time_on = true;
        timer = setInterval(function() {
            current_time--;
            updateScreen();
        }, 1000);
    } else {
        time_on = false;
        clearInterval(timer);
    }
}


class Tabata extends Component {
    constructor(props) {
        super(props);
        this.state = {
            session_length: 60,
            break_length: 20,
            num_sessions: 5,
            break_on: false,
            time_on: false,
            current_time: 0,
            current_session: 0
        };

        clearInterval(timer);
        this.startTimer = this.startTimer.bind(this);
        this.stopTimer = this.stopTimer.bind(this);
        this.updateScreen = this.updateScreen.bind(this);
        //Settings configuration

    }

    updateScreen() {
        let l;

    }

    startTimer() {
        this.setState({
            time: this.state.time,
            start: Date.now() - this.state.time,
            time_on: true
        });

    }
    stopTimer() {
        this.setState({time_on: false});
        clearInterval(this.timer)
    }
    resetTimer() {
        this.setState({time: 0})
    }

    render() {

        let start = (this.state.time === 0) ?
            <button onClick={this.startTimer}>start</button> :
            null;
        let stop = (this.state.isOn) ?
            <button onClick={this.stopTimer}>stop</button> :
            null;
        let reset = (this.state.time !== 0 && !this.state.time_on) ?
            <button onClick={this.resetTimer}>reset</button> :
            null;
        let resume = (this.state.time !== 0 && !this.state.time_on) ?
            <button onClick={this.startTimer}>resume</button> :
            null;

        return (
                <div>
                    <div id="content">
                        <div id="options">
                            <div className="timer-option">
                                <div>Ejercicio</div>
                                <button className="length-setter">-</button>
                                <input type="text" name="work-length" value={this.state.session_length}/>
                                    <button className="length-setter">+</button>
                            </div>

                            <div className="timer-option">
                                <div>Preparación</div>
                                <button className="length-setter">-</button>
                                <input type="text" name="short-break-length" value={this.state.break_length}/>
                                    <button className="length-setter">+</button>
                            </div>

                            <div className="timer-option">
                                <div>Descanso</div>
                                <button className="length-setter">-</button>
                                <input type="text" name="long-break-length" value={this.state.break_length}/>
                                    <button className="length-setter">+</button>
                            </div>
                        </div>

                        <div>
                            <div id="timer-container">
                                <span id="timer" className="large">
                                    {this.state.current_time}
                                </span>
                                <canvas id="timer-canvas" width="240" height="240"/>
                            </div>
                        </div>
                    </div>
                </div>
        );
    }
}
export default Tabata;
