import React, {Component} from "react";
import '../tabata.css';

class Timer extends Component {
    constructor(props) {
        super(props);
        this.state = {
            current_time: 10,
            break_length: 20,
            num_sessions: 5,
        };
    }

    componentDidMount(){
        const current_time = this.state.current_time;
            this.myInterval = setInterval(()=> {
            this.setState(prevState =>({
                current_time: prevState.current_time - 1
            }));
                if (current_time === 0) {
                    clearInterval(this.myInterval);
                    alert("Reached zero");
                }
        }, 1000);


    }
    render() {
        const current_time = this.state.current_time;
        return(
            <div>
                <div id="timer-container">
                                <span id="timer"  className="large" onClick={"start"}>
                                    {current_time}
                                </span>
                    <canvas id="timer-canvas" width="240" height="240"/>
                    <button onClick={clearInterval(myInterval)}>Stop</button>
                </div>
            </div>
        );
    }
}

export default Timer;
