import React, {Component} from "react";
import './tabata.css';
import './components/formInput.jxs'

// declare all variables
let session_length;
let break_length;
let num_sessions;
let current_session;
let time_on;
let current_time;
let timer;
let break_on;



class Tabata extends Component {
    constructor(props) {
        super(props);
        this.state = {
            session_length: 60,
            break_length: 30,
            time_on: false,
            current_session: 0,
            num_sessions: 0,
            break_on: false,
            current_time: 0
        };

        this.startTimer = this.startTimer.bind(this);
        this.stopTimer = this.stopTimer.bind(this);
        this.resetTimer = this.resetTimer.bind(this);
    }

    startTimer() {
        this.setState({
            time: this.state.time,
            start: Date.now() - this.state.time,
            isOn: true
        })
        this.timer = setInterval(() => this.setState({
            time: Date.now() - this.state.start
        }), 1);
    }
    stopTimer() {
        this.setState({isOn: false})
        clearInterval(this.timer)
    }
    resetTimer() {
        this.setState({time: 0})
    }

    render() {

        let start = (this.state.time === 0) ?
            <button onClick={this.startTimer}>start</button> :
            null;
        let stop = (this.state.isOn) ?
            <button onClick={this.stopTimer}>stop</button> :
            null;
        let reset = (this.state.time !== 0 && !this.state.isOn) ?
            <button onClick={this.resetTimer}>reset</button> :
            null;
        let resume = (this.state.time !== 0 && !this.state.isOn) ?
            <button onClick={this.startTimer}>resume</button> :
            null;

        return (
                <div>
                    <div id="content">
                        <div id="options">
                            <div className="timer-option">
                                <div>Ejercicio</div>
                                <button className="length-setter">-</button>
                                <input type="text" name="work-length" value="60"/>
                                    <button className="length-setter">+</button>
                            </div>

                            <div className="timer-option">
                                <div>Preparación</div>
                                <button className="length-setter">-</button>
                                <input type="text" name="short-break-length" value="10"/>
                                    <button className="length-setter">+</button>
                            </div>

                            <div className="timer-option">
                                <div>Descanso</div>
                                <button className="length-setter">-</button>
                                <input type="text" name="long-break-length" value="15"/>
                                    <button className="length-setter">+</button>
                                {start}
                                {resume}
                                {stop}
                                {reset}
                            </div>
                        </div>

                        <div>
                            <div id="timer-container">
                                <span id="timer" className="large">
                                        25:00
                                </span>
                                <canvas id="timer-canvas" width="240" height="240"/>
                            </div>
                        </div>
                    </div>
                </div>
        );
    }
}
export default Tabata;
